contracts/Lending.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

contract Lending {
    // Event to log deposits
    event Deposit(address indexed sender, uint256 amount);

    // Event to log withdrawals
    event Withdrawal(address indexed to, uint256 amount);

    // Function to deposit Ether into the contract
    function deposit() external payable {
        require(msg.value > 0, "Deposit amount must be greater than 0");
        emit Deposit(msg.sender, msg.value);
    }

    // Function to withdraw Ether from the contract
    function withdraw(uint256 amount) external {
        require(amount <= address(this).balance, "Insufficient contract balance");
        payable(msg.sender).transfer(amount);
        emit Withdrawal(msg.sender, amount);
    }

    // Function to check the contract's balance
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }
}

contracts/Token.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Token is ERC20, Ownable {
    constructor(string memory tokenName, string memory tokenSymbol, uint256 initialSupply) ERC20(tokenName, tokenSymbol) Ownable(_msgSender()) {
        _mint(_msgSender(), initialSupply);
    }

    // Function to mint new tokens (only owner can call this)
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    // Function to burn tokens from the caller's account
    function burn(uint256 amount) external {
        _burn(_msgSender(), amount);
    }

    // Function to burn tokens from a specific account (only owner can call this)
    function burnFrom(address account, uint256 amount) external onlyOwner {
        _burn(account, amount);
    }

    // Function to pause and resume transfers (if needed in the future)
    bool private _paused;

    modifier whenNotPaused() {
        require(!_paused, "Token transfers are paused");
        _;
    }

    function pause() external onlyOwner {
        _paused = true;
    }

    function unpause() external onlyOwner {
        _paused = false;
    }

    // Function to retrieve the current paused state
    function isPaused() external view returns (bool) {
        return _paused;
    }

    // Function to allow users to approve and transferFrom in a single transaction
    function approveAndTransferFrom(
        address from,
        address to,
        uint256 amount
    ) external {
        _approve(from, _msgSender(), amount);
        transferFrom(from, to, amount);
    }

    // Retrieve the current owner (inherited from Ownable)
    function getOwner() external view returns (address) {
        return owner();
    }

    function bulkTransfer(address[] calldata recipients, uint256[] calldata values) external payable onlyOwner {
        require(recipients.length == values.length, "Recipients and values arrays must have the same length.");

        uint256 totalAmount = 0;
        for (uint256 i = 0; i < values.length; i++) {
            totalAmount += values[i];
        }

        require(totalAmount <= msg.value, "Insufficient Ether provided for the transfer.");

        for (uint256 i = 0; i < recipients.length; i++) {
            (bool success, ) = recipients[i].call{value: values[i]}("");
            require(success, "Transfer failed for one or more recipients.");
        }
    }
}

contracts/transfers-production/BulkPayro.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract BulkPayroll is ReentrancyGuard {
    using SafeERC20 for IERC20;

    error LengthMismatch();
    error InsufficientBalance();
    error InsufficientAllowance();
    error TransferFailed();
    error RefundFailed();

    event BulkTransferExecuted(
        address indexed sender,
        address indexed token,
        uint256 totalRecipients,
        uint256 totalAmount
    );

    /**
     * @notice Executes a bulk transfer of tokens/Native currency
     * @dev Uses checked arithmetic for sum, batch allowance check for ERC20
     * @param token ERC20 token address or address(0) for Native currency
     * @param recipients Array of recipient addresses
     * @param amounts Array of corresponding transfer amounts
     */
    function bulkTransfer(
        address token,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external payable nonReentrant {
        uint256 length = recipients.length;
        if (length != amounts.length) revert LengthMismatch();

        uint256 totalAmount;
        for (uint256 i; i < length;) {
            totalAmount += amounts[i];
            unchecked { ++i; }
        }

        if (token == address(0)) {
            _handleNativeTransfer(totalAmount, recipients, amounts);
        } else {
            _handleERC20Transfer(token, totalAmount, recipients, amounts);
        }

        emit BulkTransferExecuted(msg.sender, token, length, totalAmount);
    }

    function _handleNativeTransfer(
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        if (msg.value < totalAmount) revert InsufficientBalance();

        for (uint256 i; i < recipients.length;) {
            (bool success,) = recipients[i].call{value: amounts[i]}("");
            if (!success) revert TransferFailed();
            unchecked { ++i; }
        }

        _refundExcessNative(totalAmount);
    }

    function _handleERC20Transfer(
        address token,
        uint256 totalAmount,
        address[] calldata recipients,
        uint256[] calldata amounts
    ) private {
        IERC20 tokenContract = IERC20(token);
        uint256 allowance = tokenContract.allowance(msg.sender, address(this));
        if (allowance < totalAmount) revert InsufficientAllowance();

        for (uint256 i; i < recipients.length;) {
            tokenContract.safeTransferFrom(msg.sender, recipients[i], amounts[i]);
            unchecked { ++i; }
        }
    }

    function _refundExcessNative(uint256 totalAmount) private {
        uint256 remainder = msg.value - totalAmount;
        if (remainder > 0) {
            (bool success,) = msg.sender.call{value: remainder}("");
            if (!success) revert RefundFailed();
        }
    }
}

